from tkinter import *root = Tk()root.title("Project 148")root.geometry("400x400")import randomList = ["hat" , "Sandwiches" , "volleyball" , "sunscreen" , "phone" , "Pen" , "Headphones" , "paper" , "tools()eraser sharpener etc" , "water bottle"]def Chooose():    rand_num = random.randint(1 , 10)    label_result["text"] = "Put item no " + str(rand_num) + " In The Bag"    label_list = Label(root)label_list["text"] = "Listed Items = " + str(List)button = Button(root , text = "Which Item Should I Put In The Bag?" , command = Chooose)label_result = Label(root)label_list.place(relx = 0.5 , rely = 0.4 , anchor = CENTER)button.place(relx = 0.5 , rely = 0.5 , anchor = CENTER)label_result.place(relx = 0.5 , rely = 0.6 , anchor = CENTER)root.mainloop()